action.code:
# check if he can land there
tile = subject.getTile(dx, dy)
if not tile.get('ground'):
    raise RuntimeError('entity tries to step on no ground')
if not tile.get('ground').attr('standable'):
    raise RuntimeError('entity tries to step on non-standable')

try:
    subject.move(dx, dy)
except PositionTakenError:
    warning('trying to step on taken tile')
    _return = False
else:
    _return = True

# waste energy
try:
    subject.changeNumericAttr('hungry', 0.1)
except:
    warning('subject has no hunger')

# process traps
traps = tile.get('trap')
if traps:
    if not traps.iterable():
        traps = [traps]
    for trap in traps:
        # TODO: random
        action('apply', {'subject' : trap, 'target' : subject})
